mapper.select:

        where d.scan_flag = '0'

			AND org_code = #{orgCode}


			AND parent_org_code = #{parentOrgCode}


			AND org_name like concat('%', #{orgName}, '%')


			AND orgStatus = #{orgStatus}


		${params.dataScope}
		order by d.parent_org_code, d.view_index

mapper.update:
update usr_org
parent_org_code = #{parentOrgCode}, org_name = #{orgName}, ancestors = #{ancestors}, view_index = #{viewIndex}, org_phone = #{orgPhone}, #{orgAddress}, orgStatus = #{orgStatus}, update_by = #{updateBy}, update_time = sysdate()
where org_code = #{orgCode}

update usr_org set ancestors =

    when #{item.orgCode} then #{item.ancestors}

where org_code in

    #{item.orgCode}
